import React, { Fragment, useEffect } from 'react'
//useEffect:when this profile loads, we need to call that getProfile action.
import PropTypes from 'prop-types'
import Spinner from '../layout/Spinner'
import { getProfile } from '../../actions/profile'
import { connect } from 'react-redux'

const Profiles = ({ getProfile, profile: { profile, loading } }) => {
  useEffect(() => {
    getProfile()
  }, []) //[] empty means just call getProfile once

  return <div />
}

Profiles.propTypes = {
  getProfile: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
}

const mapStateToProps = (state) => ({
  profile: state.profile,
})

export default connect(mapStateToProps, { getProfile })(Profiles)
